<?phprequire 'classes/class.main.php';$p = new serviceAreaMapping();if( isset($_REQUEST['type']) ){	switch( $_REQUEST['type'] ){		case "address":			$g = new stdClass();			$g->type = "address";			$g->latitude = $_REQUEST['latitude'];			$g->longitude = $_REQUEST['longitude'];			$p->getServiceAreas($g);		break;		case "zip":			$g = new stdClass();			$g->type = $_REQUEST['type'];			$g->zip = $_REQUEST['zip'];			$p->getServiceAreas($g);					break;		case "county":			$g = new stdClass();			$g->type = $_REQUEST['type'];			$g->county = $_REQUEST['county'];			$g->state = $_REQUEST['state'];			$p->getServiceAreas($g);					break;	}}?>